package vavi.math.factor.generator;

import java.math.BigInteger;

import org.junit.jupiter.api.Test;

import vavi.math.StringUtil;

import static org.junit.jupiter.api.Assertions.assertEquals;


/**
 * GoedelTest. 
 *
 * @author <a href="mailto:umjammer@gmail.com">Naohide Sano</a> (umjammer)
 * @version 0.00 2012/11/02 umjammer initial version <br>
 */
public class GoedelGeneratorTest {

    static final String actual = "[1, 2, 2, 1, 3, 72, 6, 2, 1, 3, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 3, 2, 2, 3, 2" +
            "9, 6, 2, 1, 3, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 4, 2, 3, 3, 7, 6, 2, 1, 3, 1, 1, 1," +
            " 4, 1, 7, 2, 1, 5, 1, 5, 2, 4, 3, 0, 6, 2, 1, 3, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 6" +
            ", 2, 5, 3, 3, 6, 2, 1, 3, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 7, 2, 6, 3, 67, 6, 2, 1," +
            " 4, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 8, 2, 7, 3, 12, 6, 2, 1, 4, 1, 1, 1, 4, 1, 7," +
            " 2, 1, 5, 1, 9, 2, 8, 3, 87, 6, 2, 1, 3, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 10, 2, 9," +
            " 3, 8, 6, 2, 1, 4, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 11, 2, 10, 3, 3, 6, 2, 1, 3, 1," +
            " 1, 1, 4, 1, 7, 2, 1, 5, 1, 12, 2, 11, 3, 6, 6, 2, 1, 4, 1, 1, 1, 4, 1, 7, 2, 1," +
            " 5, 1, 13, 2, 12, 3, 8, 6, 2, 1, 4, 1, 1, 1, 4, 1, 7, 2, 1, 5, 1, 14]";

    @Test
    public void test() {
        BigInteger n = new BigInteger("");
        System.err.printf("%s: \n", n);
        GoedelGenerator goedels = new GoedelGenerator(n);
        assertEquals(actual, StringUtil.toSequence(goedels));
    }

    /**
     * @param args
     */
    public static void main(String[] args) throws Exception {
        BigInteger n = new BigInteger(args[0]);
        System.err.printf("%s: \n", n);
        GoedelGenerator goedels = new GoedelGenerator(n);
        long t = System.currentTimeMillis();
        System.err.println(StringUtil.toSequence(goedels).equals(actual) + ", in " + (System.currentTimeMillis() - t) + " ms");
    }
}

